Component({

  properties: {

    /*!
    ** 是否滚动到顶部就粘黏。
    */
    alwaysOnTop: {
      type: Boolean,
      value: false,
    },

    items: {
      type: Array,
      value: [],
    },

  },

  data: {

    currentTabIndex: 0,

  },

  lifetimes: {
    attached() {
      
    }
  },

  methods: {

    doClickTabItem(ev) {
      let index = ev.target.dataset.current;
      this.setData({
        currentTabIndex: index,
      });
      this.triggerEvent('didClickTabItem', {
        index: index,
      });
    },
    
  }
});